window.screenRatio=window.innerHeight/window.innerWidth;window.PI=Math.PI;window.pi=Math.PI;window.rand=(n=0,t=1)=>Math.random()*(t-n)+n;window.sin=(n=time,t=1,i=1)=>Math.sin(n*i)*t;window.cos=(n=time,t=1,i=1)=>Math.cos(n*i)*t;window.tan=(n=time,t=1,i=1)=>Math.tan(n*i)*t;window.saw=function(n=time,t=1,i=1){return-(2*t/PI)*Math.atan(1/Math.tan(time*PI/(1/i)))};window.bounce=function(n=time,t=3,i=1,w=8){var o=()=>6+saw(time,6,i/2)/2;return Math.pow(Math.E,1-o())*Math.abs(t*Math.cos(w*o()))};window.sint=(...n)=>sin(time,...n);window.cost=(...n)=>cos(time,...n);window.tant=(...n)=>tan(time,...n);window.sawt=(...n)=>saw(time,...n);window.bouncet=(...n)=>bounce(time,...n);window.osctorange=(n,t,i)=>(.5+n/2)*(i-t)+t;window.sinrange=(n=time,t=0,i=1,w=1)=>osctorange(Math.sin(n*w),t,i);window.cosrange=(n=time,t=0,i=1,w=1)=>osctorange(Math.cos(n*w),t,i);window.sawrange=(n=time,t=0,i=1,w=1)=>osctorange(saw(n,1,w),t,i);
